.input-field {
  width: 100%;
  display: -webkit-box;
  display: flex;
  -webkit-box-orient: vertical;
  -webkit-box-direction: normal;
  flex-direction: column;
  margin-bottom: 20px;
  flex-wrap: wrap;
}

.input-field--column-field {
  width: 45%;
}

.input-field--split-column {
  -webkit-box-orient: horizontal;
  -webkit-box-direction: normal;
  flex-direction: row;
  -webkit-box-pack: justify;
  justify-content: space-between;
  flex-wrap: wrap;
}

.input-field--split-column .booking__form__label::after {
  content: " : ";
  margin-right: 5px;
}

.booking__form__label {
  font-weight: bold;
  font-size: 20px;
  font-family: inherit;
  margin-bottom: 8px;
}

.progress {
  padding: 10px;
  margin-bottom: 10px;
}

.progress__element {
  width: 100%;
  height: 7px;
  -webkit-transition-duration: 0.4s;
  transition-duration: 0.4s;
}

.progress__element[value] {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
}

.progress__element[value]::-webkit-progress-bar {
  background: transparent;
  border-radius: 2px;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.25) inset;
  background-size: 35px 20px, 100% 100%, 100% 100%;
}

.progress__element[value]::-webkit-progress-value {
  background-color: black;
  border-radius: 2px;
  -webkit-transition: width 0.2s ease;
  transition: width 0.2s ease;
}

.booking__form__input-element {
  border: solid 1px #d6d3d3;
  border-radius: 3px;
  padding: 7px 5px;
  background: transparent;
}

.each__stage__action-button {
  display: -webkit-box;
  display: flex;
  -webkit-box-pack: justify;
  justify-content: space-between;
}

.booking__form_each-form .each-stage__button {
  border-style: solid 1px transparent;
  font-weight: bolder;
  background-color: black;
  color: white;
  -webkit-transition: width 0.4s ease-in-out;
  transition: width 0.4s ease-in-out;
  padding: 10px;
  border-radius: 3px;
  box-shadow: 2px 2px 5px rgba(0, 0, 0, 0.25);
}

.each-stage__button--back {
  align-self: center;
}

.estimate-button--modify {
  padding: 0 !important;
  background-color: wheat;
}

.loading-gif {
  width: 50px;
  height: 50px;
}

.confirmation__section {
  font-weight: bold;
}

.confirmation__container__estimate-details {
  padding-bottom: 20px;
}

@media screen and (max-width: 800px) {
  .booking__container {
    width: 90%;
  }

  .each-tab {
    text-align: center;
  }
}
@media screen and (max-width: 600px) {
  .input-field--column-field {
    width: 100%;
  }
}
@media screen and (max-width: 450px) {
  .booking__container {
    width: 98%;
    padding: 10px 10px 20px;
  }

  .booking__icon {
    font-size: 20px;
  }
}

